def helper(board, i, j, word, k):
    if k >= len(word):
        return True

    if i >= len(board) or i < 0 or j >= len(board[0]) or j < 0:
        return False

    if not board[i][j] or board[i][j] != word[k]:
        return False

    val = board[i][j]
    board[i][j] = None

    result1 = helper(board, i+1, j, word, k+1)
    if result1:
        return result1
    result2 = helper(board, i-1, j, word, k+1)
    if result2:
        return result2
    result3 = helper(board, i, j+1, word, k+1)
    if result3:
        return result3
    result4 = helper(board, i, j-1, word, k+1)
    if result4:
        return result4

    board[i][j] = val

    return result1 \
        or result2 \
        or result3 \
        or result4

def exist(board, word):
    if not word:
        return True

    if not board:
        return False

    result = False
    for i in range(len(board)):
        for j in range(len(board[0])):
            if board[i][j] == word[0]:
                result = result or helper(board, i, j, word, 0)

    return result


